targets.expr:
(
  sum by (prometheus, namespace, phase) (
    increase(argo_workflows_total_count{phase!~"Failed"}[1m])
  ) > 0
)
unless on (prometheus, namespace, phase)
(
  sum by (prometheus, namespace, phase) (
    increase(argo_workflows_total_count{phase!~"Failed"}[1m] offset 1h)
  ) > 0
)

(
  sum by (prometheus, namespace, phase) (
    increase(argo_workflows_total_count{phase="Failed"}[1m])
  ) > 0
)
unless on (prometheus, namespace, phase)
(
  sum by (prometheus, namespace, phase) (
    increase(argo_workflows_total_count{phase="Failed"}[1m] offset 1h)
  ) > 0
)

(
  sum by (prometheus, namespace) (
    max_over_time(argo_workflows_pod_pending_count{prometheus=~"^$dc$", namespace=~"$ns"}[1m])
  ) > 0
)
unless on (prometheus, namespace)
(
  sum by (prometheus, namespace) (
    max_over_time(argo_workflows_pod_pending_count{prometheus=~"^$dc$", namespace=~"$ns"}[1m] offset 1d)
  ) > 0
)

topk(
  5,
  clamp_min(
    sum by (reason, prometheus, namespace) (
      max_over_time(argo_workflows_pod_pending_count{prometheus=~"^$dc$", namespace=~"$ns"}[$ts])
    )
  -
    sum by (reason, prometheus, namespace) (
      max_over_time(argo_workflows_pod_pending_count{prometheus=~"^$dc$", namespace=~"$ns"}[$ts] offset 1d)
    ),
    0
  )
)
