targets.expr:
round(
  sum by (pod) (
    container_memory_working_set_bytes{
      namespace="truefoundry",
      container!="POD",
      container!="",
      pod!="",
      pod=~".*-tfy-llm-gateway-infra-clickhouse.*"
    }
  ) / 1000000, 
  0.001
)

round(
  sum by (pod) (
    kube_pod_container_resource_requests{
      job="kube-state-metrics",
      resource="memory",
      namespace="truefoundry",
      pod=~".*-tfy-llm-gateway-infra-clickhouse.*"
    }
  ) / 1000000, 
  0.001
)

sum by (pod) (
  kube_pod_container_resource_limits{
    job="kube-state-metrics",
    resource="memory",
    namespace="truefoundry",
    pod=~".*-tfy-llm-gateway-infra-clickhouse.*"
  }
) / 1000000

round(
  sum by (pod) (
    rate(container_cpu_usage_seconds_total{
      namespace="truefoundry",
      container!="POD",
      container!="",
      pod=~".*-tfy-llm-gateway-infra-clickhouse.*",
    }[$__rate_interval])
  ), 
  0.001
)

round(
  sum by (pod) (
    kube_pod_container_resource_limits{
      job="kube-state-metrics",
      resource="cpu",
      namespace="truefoundry",
      pod=~".*-tfy-llm-gateway-infra-clickhouse.*"
    }
  ), 
  0.001
)

round(
  sum by (pod) (
    kube_pod_container_resource_limits{
      job="kube-state-metrics",
      resource="cpu",
      namespace="truefoundry",
      pod=~".*-tfy-llm-gateway-infra-clickhouse.*"
    }
  ), 
  0.001
)
