targets.expr:
round((max by (container) (
  max by (namespace, pod, container) (
    rate(
      container_cpu_cfs_throttled_periods_total{
        job="kubelet",
        container!="", container!="POD", container!="init", container!="wait",
        namespace="$namespace", pod!="", pod=~"$podpattern"
      }[$__rate_interval]
    )
    /
    rate(
      container_cpu_cfs_periods_total{
        job="kubelet",
        container!="", container!="POD", container!="init", container!="wait",
        namespace="$namespace", pod!="", pod=~"$podpattern"
      }[$__rate_interval]
    )
  )
)) * 100, 1)
